import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../../../context/UserContext';
import { toast } from 'react-hot-toast';
import { MdDeleteOutline } from 'react-icons/md';
import axios from 'axios';
import BtnSpinner from '../../Shared/Loading/BtnSpinner';
import { Navigate } from 'react-router-dom';
import ShowQuestionPagination from './ShowQuestionPagination';
import ShowUnknownQuestionPagination from './ShowUnknownQuestionPagination';
const Account = () => {

  const QuestionPerPage = 25;

  //use useeContext to load data from  another components
  const { logOut, userInfo, SocketDisconnect, loading, setLoading, user, setUser, totalQuestions, translationQuestions, setTotalQuestions, setTranslationQuestions, translateCalculatePercentage, unknownCalculatePercentage, unknownPercent, setUnknownPercent, translationPercent, setTranslationPercent, unknownQuestions, setUnknownQuestions } = useContext(AuthContext);


  console.log(userInfo);


  //got the current user data from database  and get the data after getting the information about the current user 
  useEffect(() => {
    if (user?.email) {
      fetchQuestionsByEmail();
      fetchUnknownQuestionsByEmail();
      translationsQuestionsByEmail();
    }
  }, [user?.email]);





  //fetch all searching question which questions are search by this specific users
  const fetchQuestionsByEmail = async () => {
    try {
      const response = await axios.get('https://grozziieget.zjweiting.com:8033/tht/questions', {
        params: {
          email: user?.email,
        },
      });
      setTotalQuestions(response.data);
      if (totalQuestions) {
        setUnknownPercent(unknownCalculatePercentage(totalQuestions, unknownQuestions));
        setTranslationPercent(translateCalculatePercentage(totalQuestions, translationQuestions));
      }
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };



  //fetch all unknown question which questions are search by this specific users
  const fetchUnknownQuestionsByEmail = async () => {
    try {
      const response = await axios.get('https://grozziieget.zjweiting.com:8033/tht/unknownQuestions', {
        params: {
          email: user?.email,
        },
      });
      setUnknownQuestions(response.data);
      if (unknownQuestions) {
        setUnknownPercent(unknownCalculatePercentage(totalQuestions, unknownQuestions));

      }
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };



  //fetch all translation problem question which questions are search by this specific users
  const translationsQuestionsByEmail = async () => {
    try {
      const response = await axios.get('https://grozziieget.zjweiting.com:8033/tht/translationsQuestions', {
        params: {
          email: user?.email,
        },
      });
      setTranslationQuestions(response.data);
      if (translationQuestions) {
        setTranslationPercent(translateCalculatePercentage(totalQuestions, translationQuestions));

      }
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };



  //create a function to LogOut user from this site
  const handleToLogOut = () => {
    try {
      setLoading(true);
      SocketDisconnect();
      setUser(null);
      localStorage.removeItem('chattingUser');
      localStorage.removeItem('user');
      toast.success("Logout successfully");
      setLoading(false);
    }
    catch (err) {
      toast.error(err)
    }


  }




  // Here create the function to delete all question search by this specific user
  const handleToDeleteAllData = async () => {
    try {
      const confirmed = window.confirm('Are you sure you want to delete all questions?');
      if (!confirmed) {
        return; // Cancel the deletion if the user clicks Cancel or closes the modal
      }

      await axios.delete(`https://grozziieget.zjweiting.com:8033/tht/questions/delete/${user?.email}`);
      toast.success('All questions deleted successfully');
      setTotalQuestions(totalQuestions.filter((question) => question?.email !== user?.email));
      setUnknownPercent(unknownCalculatePercentage(totalQuestions, unknownQuestions));
      setTranslationPercent(translateCalculatePercentage(totalQuestions, translationQuestions));
    } catch (error) {
      console.error('Error deleting user:', error);
      toast.error('Failed to delete user');
    }
  };

  //create a function to delete all unknown question from the frontend and database both side 
  const handleToDeleteUnknownData = async () => {
    try {
      const confirmed = window.confirm('Are you sure you want to delete all unknown question?');
      if (!confirmed) {
        return;
      }
      await axios.delete(`https://grozziieget.zjweiting.com:8033/tht/unknownQuestions/delete/${user?.email}`);
      toast.success('All unknown questions deleted successfully');
      setUnknownQuestions([]);
      setUnknownPercent(unknownCalculatePercentage(totalQuestions, unknownQuestions));
      setTranslationPercent(translateCalculatePercentage(totalQuestions, translationQuestions));
    } catch (error) {
      console.error('Error deleting user:', error);
      toast.error('Failed to delete user');
    }
  };

  //create a function to delete one by one unknown question 
  const handleToDeleteOneUnknownQuestions = async (id) => {
    try {
      const confirmed = window.confirm('Are you sure you want to delete this unknown question?');
      if (!confirmed) {
        return;
      }
      await axios.delete(`https://grozziieget.zjweiting.com:8033/tht/unknownQuestions/deleteById/${id}`);
      toast.success('A unknownQuestion deleted successfully');
      setUnknownQuestions(unknownQuestions.filter((question) => question?.id !== id));
      setUnknownPercent(unknownCalculatePercentage(totalQuestions, unknownQuestions));
      setTranslationPercent(translateCalculatePercentage(totalQuestions, translationQuestions));
    } catch (error) {
      console.error('Error deleting user:', error);
      toast.error('Failed to delete user');
    }
  }


  //create a function to delete all unknown question from the frontend and database both side 
  const handleToDeleteTranslateData = async () => {
    try {
      const confirmed = window.confirm('Are you sure you want to delete all translations question?');
      if (!confirmed) {
        return;
      }
      await axios.delete(`https://grozziieget.zjweiting.com:8033/tht/translateData/delete/${user?.email}`);
      toast.success('All translate questions deleted successfully');
      setTranslationQuestions([]);
      setUnknownPercent(unknownCalculatePercentage(totalQuestions, unknownQuestions));
      setTranslationPercent(translateCalculatePercentage(totalQuestions, translationQuestions));
    } catch (error) {
      console.error('Error deleting user:', error);
      toast.error('Failed to delete user');
    }
  };


  //create a function to delete one by one unknown question from the frontend and database both side 
  const handleToDeleteTranslationQuestions = async (id) => {
    try {
      const confirmed = window.confirm('Are you sure you want to delete this translation question?');
      if (!confirmed) {
        return;
      }
      await axios.delete(`https://grozziieget.zjweiting.com:8033/tht/translationQuestions/deleteById/${id}`);
      toast.success('A Translation deleted successfully');
      setTranslationQuestions(translationQuestions.filter((question) => question?.id !== id));
      setUnknownPercent(unknownCalculatePercentage(totalQuestions, unknownQuestions));
      setTranslationPercent(translateCalculatePercentage(totalQuestions, translationQuestions));
    } catch (error) {
      console.error('Error deleting user:', error);
      toast.error('Failed to delete user');
    }
  }


  return (
    <div className="mt-32 md:mx-36 text-gray-800">
      <div className="grid grid-cols-1 md:grid-cols-2 text-center">

        {/* create this part to show th user pic */}
        <div className="flex justify-around">
          {userInfo?.image ?
            <img className="rounded-full h-56 w-56" src={userInfo?.image}></img>
            :
            <img className="rounded-full h-56 w-56" src="data:image/png;base64,"></img>

          }
        </div>

        {/* Here is the part to show all information about the current user */}
        <div className="text-start mt-20 md:mt-0 ">
          <div className="ml-20 md:ml-0 mx-auto md:mx-0">
            <div className="test-center">
              <h2 className="text-lg font-semibold mb-0 pb-0">
                {userInfo?.name}
              </h2>
              <p className="mb-4 ml-1">
                {userInfo ? userInfo?.designation : "Designation"}
              </p>
            </div>
            <div className="grid grid-cols-4 my-3">
              <p className="font-semibold">
                Contact Number:
              </p>
              <p >{userInfo?.phone}</p>
            </div>
            <div className="grid grid-cols-4 my-3">
              <p className="font-semibold">
                Email
              </p>
              <p>{user?.email}</p>
            </div>
            <div className="grid grid-cols-4 my-3">
              <p className="font-semibold">
                Country
              </p>
              <p>{userInfo ? userInfo?.country : "smzubayer9004@gmail.com"}</p>
            </div>
            <div className="grid grid-cols-4 my-3">
              <p className="font-semibold">
                Language
              </p>
              <p>{userInfo ? userInfo?.language : "Bengali"}</p>
            </div>
            <div className="my-20 mb-10">
              <button onClick={handleToLogOut} className="bg-[#004368]  px-20 rounded-md py-2 ml-2 text-white font-semibold hover:bg-slate-800" >
                {
                  loading ?
                    <BtnSpinner></BtnSpinner>
                    : "Log out"
                }
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Show the all question, unknown question and translation problem question according to the specific user according to the pagination order */}
      <div className="mb-10 text-base">
        <ShowQuestionPagination
          handleToDeleteAllData={handleToDeleteAllData}
          totalQuestions={totalQuestions}
          QuestionPerPage={QuestionPerPage}
        ></ShowQuestionPagination>



        <ShowUnknownQuestionPagination
          handleToDeleteUnknownData={handleToDeleteUnknownData}
          unknownQuestions={unknownQuestions}
          handleToDeleteOneUnknownQuestions={handleToDeleteOneUnknownQuestions}
          unknownPercent={unknownPercent}
        ></ShowUnknownQuestionPagination>

        {/* create this part to show all of the store translation data which translation didn't get properly*/}

        <div className="flex justify-between text-3xl bg-red-200 py-1 font-bold mt-20 px-4">
          <h2 className="">Translation Questions</h2>
          <div className="flex items-center">
            <p >{translationPercent}% </p>
            <MdDeleteOutline onClick={handleToDeleteTranslateData} className='ml-5 text-2xl cursor-pointer'></MdDeleteOutline>
          </div>
        </div>


        <div>
          <table className="w-full">
            <thead>
              <tr className="bg-orange-200">
                <th>No.</th>
                <th>Question</th>
                <th>English</th>
                <th>Bengali</th>
                <th>Time</th>
                <th>Date</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {
                translationQuestions?.length !== 0 ? translationQuestions?.map((item, index) => (
                  <tr key={item.id}>
                    <td>{index + 1}</td>
                    <td className="text-start">{item?.question}</td>
                    <td className="text-start">{item?.english}</td>
                    <td className="text-start">{item?.bangla}</td>
                    <td>{item?.time}</td>
                    <td>{item?.date}</td>
                    <td>
                      <button>
                        <MdDeleteOutline onClick={() => handleToDeleteTranslationQuestions(item.id)} className='ml-5 text-2xl cursor-pointer'></MdDeleteOutline>
                      </button>
                    </td>
                  </tr>
                )) : <span className="text-xl font-bold text-red-400">NO Question Available</span>
              }
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Account;